div.div:
// make a new controller
ctlr = make_controller
// a controller instance is needed to direct the user input
btn_a = Button(PIN_A, Receiver::pressed_a, ctrl)
btn_b = Button(PIN_B, Receiver::pressed_b, ctrl)
// create a collection of buttons to be checked
List<Button> buttons {btn_a, btn_b}
// create a concrete screen
screen = 1in14Screen() as Screen
// request adding a view by type so that the system doesn't have
// to know any details. Internally, a concrete view will be created
// and either create or share a model that matches that view type
ctrl.add_view(ViewType::Home, screen)

while (true) {
for b in buttons: b.check_state(); // each button will trigger an event, if needed.
// The controller will have directed any updates to the models/views and the screen
// can be redrawn
controller.draw_current_view();
// may not ever need to wait. Just GO!
wait(100us)
}
